sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate)
{
	g_form.addInfoMessage("Function onChange() started");

	if ((isLoading && !g_form.isNewRecord()) || newValue === '' || oldValue == newValue)
    {
		g_form.addInfoMessage("Exit");

		return;
    }

	var getEmailAddr = new GlideAjax('GetEmailAddress');
	getEmailAddr.addParam('sysparm_name', 'getEmail');
	getEmailAddr.addParam('sysparm_userID', g_form.getValue('u_requested_for'));
	getEmailAddr.getXML(populateEmailField);

	function populateEmailField(response)
	{
		var strEmailFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
		g_form.clearValue('txf_requested_for_email');
		g_form.setValue('txf_requested_for_email', strEmailFromScriptInclude);
	}


}
