sys_script_include.script:
var GetEmailAddress = Class.create();
GetEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor,
{
	// function getEmail
	// parameter1: sysparm_userID
	// return: EmailAddress as String
	getEmail: function()
	{
		try
		{
			var userRecord = new GlideRecord("sys_user");
			gs.info("**** sysparm_userID: {0} ******", this.getParameter('sysparm_userID'));
			userRecord.get(this.getParameter('sysparm_userID'));
			gs.info("**** userRecord.Name: {0} ******", userRecord.name);
			gs.info("**** userRecord.Email: {0} ******", userRecord.email);
			return userRecord.email + '';
		}
		catch(err)
		{
			gs.info("**** ERROR: {0} ****", err.message);
			return null;
		}
	},
    type: 'GetEmailAddress'
});
