sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(date)
	{
		var dateRightNow = new GlideDateTime();
		var dateTesting = new GlideDateTime(date);

		if (dateTesting.before(dateRightNow))
		{
			return(true);
		}
		else
		{
			return(false);
		}

	},

	isDateToday: function(date)
	{
		var dateRightNow = new GlideDateTime();
		var dateTesting = new GlideDateTime(date);

		if (dateRightNow.getLocalDate().getDisplayValue() === dateTesting.getLocalDate().getDisplayValue())
		{
			return(true);
		}
		else
		{
			return(false);
		}
	},

    type: 'NeedItUtils'
};
